// 페이지 로드 시 저장된 배경색과 로고 설정
document.addEventListener("DOMContentLoaded", () => {
    let savedColor = localStorage.getItem("bgColor");
    if (savedColor) {
        document.body.style.backgroundColor = savedColor;
        updateLogo(savedColor);
    }
});

let inputButton = document.querySelector("#name-input-btn");

// 버튼 클릭 이벤트
inputButton.addEventListener("click", () => {
    let nameInput = document.querySelector("#name-input");
    let color = nameInput.value.toLowerCase();

    // 유효한 색상인지 확인
    if (["red", "blue", "yellow"].includes(color)) {
        localStorage.setItem("bgColor", color);
        document.body.style.backgroundColor = color;
        updateLogo(color);

        let hello = document.querySelector("#hello");
        hello.textContent = color + "로 배경이 설정되었습니다.";
    } else {
        alert("유효한 색상을 입력하세요 (blue, red, yellow)");
    }
});

// 로고 이미지 업데이트 함수
function updateLogo(color) {
    let logo = document.querySelector("#logo");

    switch (color) {
        case "blue":
            logo.src = "data:image/jpeg;base64,";
            break;
        case "red":
            logo.src = "data:image/jpeg;base64,";
            break;
        case "yellow":
            logo.src = "data:image/jpeg;base64,";
            break;
        default:
            logo.src = "default-logo.png";
            break;
    }
}
